package com.example.myapplication;

import android.os.Bundle;
import android.widget.ArrayAdapter;
import android.widget.Button;
import android.widget.Spinner;
import android.widget.TextView;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import java.util.ArrayList;
import java.util.List;

public class BookingActivity extends AppCompatActivity {

    private Spinner roomTypeSpinner, availabilitySpinner, priceRangeSpinner;
    private RecyclerView roomsRecyclerView;
    private RoomAdapter roomAdapter;
    private List<Room> roomList, filteredRooms;
    private TextView filterSelectionText;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_booking);

        // Initialize the views
        roomTypeSpinner = findViewById(R.id.filterRoomTypeSpinner);
        availabilitySpinner = findViewById(R.id.filterAvailabilitySpinner);
        priceRangeSpinner = findViewById(R.id.filterPriceRangeSpinner);
        roomsRecyclerView = findViewById(R.id.roomsRecyclerView);
        Button confirmBookingButton = findViewById(R.id.filterOkButton);
        filterSelectionText = findViewById(R.id.filterSelectionText);

        // Setup spinners and RecyclerView
        setupSpinners();
        setupRoomsRecyclerView();

        // Handle "OK" button click to apply filters
        confirmBookingButton.setOnClickListener(v -> {
            String selectedRoomType = roomTypeSpinner.getSelectedItem().toString();
            String selectedAvailability = availabilitySpinner.getSelectedItem().toString();
            String selectedPriceRange = priceRangeSpinner.getSelectedItem().toString();

            // Update filter display and filter the rooms
            updateFilterSelectionText(selectedRoomType, selectedAvailability, selectedPriceRange);
            filterRooms(selectedRoomType, selectedAvailability, selectedPriceRange);
        });
    }

    private void setupSpinners() {
        ArrayAdapter<CharSequence> roomTypeAdapter = ArrayAdapter.createFromResource(
                this, R.array.room_types, android.R.layout.simple_spinner_item);
        roomTypeAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        roomTypeSpinner.setAdapter(roomTypeAdapter);

        ArrayAdapter<CharSequence> availabilityAdapter = ArrayAdapter.createFromResource(
                this, R.array.availability_options, android.R.layout.simple_spinner_item);
        availabilityAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        availabilitySpinner.setAdapter(availabilityAdapter);

        ArrayAdapter<CharSequence> priceRangeAdapter = ArrayAdapter.createFromResource(
                this, R.array.price_ranges, android.R.layout.simple_spinner_item);
        priceRangeAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        priceRangeSpinner.setAdapter(priceRangeAdapter);
    }

    private void setupRoomsRecyclerView() {
        roomList = getDefaultRooms();
        filteredRooms = new ArrayList<>(roomList);
        roomAdapter = new RoomAdapter(this, filteredRooms);
        roomsRecyclerView.setLayoutManager(new LinearLayoutManager(this));
        roomsRecyclerView.setAdapter(roomAdapter);
    }

    private List<Room> getDefaultRooms() {
        List<Room> rooms = new ArrayList<>();
        rooms.add(new Room("Deluxe Suite", "Spacious suite with ocean view", "data:image/jpeg;base64,", 150.00, true));
        rooms.add(new Room("Family Room", "Perfect for families with kids", "data:image/jpeg;base64,", 120.00, true));
        rooms.add(new Room("Single Room", "Comfortable room for one", "data:image/jpeg;base64,", 75.00, false));
        rooms.add(new Room("Double Room", "Cozy room for couples", "data:image/jpeg;base64,", 100.00, true));
        rooms.add(new Room("Presidential Suite", "Luxury suite with VIP amenities", "data:image/jpeg;base64,", 500.00, true));
        rooms.add(new Room("Budget Room", "Affordable stay for travelers", "data:image/jpeg;base64,", 50.00, true));
        rooms.add(new Room("King Room", "Premium comfort with king bed", "data:image/jpeg;base64,", 200.00, false));
        rooms.add(new Room("Queen Room", "Elegant room with queen bed", "data:image/jpeg;base64,", 180.00, true));
        rooms.add(new Room("Executive Suite", "Designed for business travelers", "data:image/jpeg;base64,", 220.00, true));
        rooms.add(new Room("Penthouse", "Exclusive stay at the top floor", "data:image/jpeg;base64,", 700.00, false));
        return rooms;
    }

    private void filterRooms(String roomType, String availability, String priceRange) {
        List<Room> filtered = new ArrayList<>();
        for (Room room : roomList) {
            boolean matchesRoomType = roomType.equals("All") || room.getName().contains(roomType);
            boolean matchesAvailability = availability.equals("All") ||
                    (availability.equals("Available") && room.isAvailable()) ||
                    (availability.equals("Unavailable") && !room.isAvailable());
            boolean matchesPriceRange = priceRange.equals("All") ||
                    (priceRange.equals("$0 - $100") && room.getPrice() <= 100) ||
                    (priceRange.equals("$101 - $200") && room.getPrice() > 100 && room.getPrice() <= 200) ||
                    (priceRange.equals("$201 - $500") && room.getPrice() > 200 && room.getPrice() <= 500) ||
                    (priceRange.equals("Above $500") && room.getPrice() > 500);

            if (matchesRoomType && matchesAvailability && matchesPriceRange) {
                filtered.add(room);
            }
        }
        filteredRooms.clear();
        filteredRooms.addAll(filtered);
        roomAdapter.notifyDataSetChanged();
    }

    private void updateFilterSelectionText(String roomType, String availability, String priceRange) {
        String filterText = "Selected Filters: \nRoom Type: " + roomType +
                "\nAvailability: " + availability + "\nPrice Range: " + priceRange;
        filterSelectionText.setText(filterText);
    }
}
